<?php
namespace yii\easyii\modules\informing\api;

use Yii;
use yii\data\ActiveDataProvider;
use yii\easyii\models\Tag;
use yii\easyii\widgets\Fancybox;
use yii\widgets\LinkPager;

use yii\easyii\modules\informing\models\Informing as InformingModel;

/**
 * Informing module API
 * @package yii\easyii\modules\informing\api
 *
 * @method static InformingObject get(mixed $id_slug) Get informing object by id or slug
 * @method static array items(array $options = []) Get list of informing as InformingObject objects
 * @method static mixed last(int $limit = 1) Get last informing
 * @method static void plugin() Applies FancyBox widget on photos called by box() function
 * @method static string pages() returns pagination html generated by yii\widgets\LinkPager widget.
 * @method static \stdClass pagination() returns yii\data\Pagination object.
 *
 * @method static mixed popular(int $limit = 1) Get popular informing
 */

class Informing extends \yii\easyii\components\API
{
    private $_adp;
    private $_last;
    private $_items;
    private $_item = [];
	private $_popular;

    public function api_items($options = [])
    {
        if(!$this->_items){
            $this->_items = [];

            $with = ['seo'];
            if(Yii::$app->getModule('admin')->activeModules['informing']->settings['enableTags']){
                $with[] = 'tags';
            }
            $query = InformingModel::find()->with($with)->status(InformingModel::STATUS_ON);

            if(!empty($options['where'])){
                $query->andFilterWhere($options['where']);
            }
            if(!empty($options['tags'])){
                $query
                    ->innerJoinWith('tags', false)
                    ->andWhere([Tag::tableName() . '.name' => (new InformingModel)->filterTagValues($options['tags'])])
                    ->addGroupBy('informing_id');
            }
            if(!empty($options['orderBy'])){
                $query->orderBy($options['orderBy']);
            } else {
                $query->sortDate();
            }

            $this->_adp = new ActiveDataProvider([
                'query' => $query,
                'pagination' => !empty($options['pagination']) ? $options['pagination'] : []
            ]);

            foreach($this->_adp->models as $model){
                $this->_items[] = new InformingObject($model);
            }
        }
        return $this->_items;
    }

    public function api_get($id_slug)
    {
        if(!isset($this->_item[$id_slug])) {
            $this->_item[$id_slug] = $this->findInforming($id_slug);
        }
        return $this->_item[$id_slug];
    }

    public function api_last($limit = 1)
    {
        if($limit === 1 && $this->_last){
            return $this->_last;
        }

        $with = ['seo'];
        if(Yii::$app->getModule('admin')->activeModules['informing']->settings['enableTags']){
            $with[] = 'tags';
        }

        $result = [];
        foreach(InformingModel::find()->with($with)->status(InformingModel::STATUS_ON)->sortDate()->limit($limit)->all() as $item){
            $result[] = new InformingObject($item);
        }

        if($limit > 1){
            return $result;
        } else {
            $this->_last = count($result) ? $result[0] : null;
            return $this->_last;
        }
    }

    public function api_plugin($options = [])
    {
        Fancybox::widget([
            'selector' => '.easyii-box',
            'options' => $options
        ]);
    }

    public function api_pagination()
    {
        return $this->_adp ? $this->_adp->pagination : null;
    }

    public function api_pages()
    {
        return $this->_adp ? LinkPager::widget(['pagination' => $this->_adp->pagination]) : '';
    }

    private function findInforming($id_slug)
    {
        $informing = InformingModel::find()->where(['or', 'informing_id=:id_slug', 'slug=:id_slug'], [':id_slug' => $id_slug])->status(InformingModel::STATUS_ON)->one();
        if($informing) {
            $informing->updateCounters(['views' => 1]);
            return new InformingObject($informing);
        } else {
            return null;
        }
    }

	public function api_popular($limit = 1)
	{
		if($limit === 1 && $this->_popular){
			return $this->_popular;
		}

		$with = ['seo'];
		if(Yii::$app->getModule('admin')->activeModules['informing']->settings['enableTags']){
			$with[] = 'tags';
		}

		$result = [];
        $lasttime = InformingModel::find()->with($with)->status(InformingModel::STATUS_ON)->orderBy('time DESC')->one();
		foreach(InformingModel::find()->with($with)->status(InformingModel::STATUS_ON)->where(['>', 'time', $lasttime->time-1186400])->orderBy('views DESC')->limit($limit)->all() as $item){
			$result[] = new InformingObject($item);
		}

		if($limit > 1){
			return $result;
		} else {
			$this->_last = count($result) ? $result[0] : null;
			return $this->_last;
		}
	}
}